import React, { useEffect, useState } from "react";
import { Grid, Paper, Typography } from "@mui/material";
import "../styles/dashboard.css";

const BalanceCards = () => {
  const [balances, setBalances] = useState({
    total: 0,
    bank: 0,
    cash: 0,
  });

  useEffect(() => {
    const token = localStorage.getItem("token"); // Get token from storage
    if (!token) {
        console.error("No token found! User is not authenticated.");
        return;
    }

    fetch("http://127.0.0.1:8000/api/v1/dashboard/balances/", {
        method: "GET",
        headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,  // <-- Make sure format is correct
        },
    })
    .then(async (res) => {
        if (!res.ok) {
            throw new Error(`HTTP Error: ${res.status}`);
        }
        return res.json();
    })
    .then((data) => {
        console.log("Fetched balance data:", data);
        setBalances({
            total: data.totalBalance,
            bank: data.totalBalance,  // Adjust if needed
            cash: data.totalBalance,
        });
    })
    .catch((err) => console.error("Error fetching balance data:", err));
}, []);

  const cardStyle = {
    padding: "20px",
    textAlign: "center",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    background: "#ffffff",
  };

  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Paper style={cardStyle}>
          <Typography variant="h5" color="primary">${balances.total.toLocaleString()}</Typography>
          <Typography variant="subtitle1">Balance Total</Typography>
        </Paper>
      </Grid>
      <Grid item xs={6}>
        <Paper style={cardStyle}>
          <Typography variant="h5" color="primary">${balances.bank.toLocaleString()}</Typography>
          <Typography variant="subtitle1">Balance Banco</Typography>
        </Paper>
      </Grid>
      <Grid item xs={6}>
        <Paper style={cardStyle}>
          <Typography variant="h5" color="primary">${balances.cash.toLocaleString()}</Typography>
          <Typography variant="subtitle1">Balance Efectivo</Typography>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default BalanceCards;
