import React, { useEffect, useState } from "react";
import { Grid, Paper, Typography, MenuItem, Select, FormControl, InputLabel } from "@mui/material";
import "../styles/dashboard.css";

const BalanceCard = () => {
    const [accounts, setAccounts] = useState([]); // Stores user accounts
    const [selectedAccount, setSelectedAccount] = useState(""); // Selected account name
    const [balance, setBalance] = useState(null); // Balance of the selected account
    const [currency, setCurrency] = useState(""); // Currency of the selected account
    const [loading, setLoading] = useState(true); // Loading state for fetching data

    // âœ… Token management - Auto refresh if needed
    const refreshToken = async () => {
        try {
            const refresh = localStorage.getItem("refresh_token");
            if (!refresh) throw new Error("No refresh token found!");

            const response = await fetch("http://127.0.0.1:8000/api/v1/auth/token/refresh/", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ refresh }),
            });

            if (!response.ok) throw new Error("Failed to refresh token");

            const data = await response.json();
            localStorage.setItem("access_token", data.access);
            console.log("âœ… Token refreshed:", data.access);
            return data.access;
        } catch (error) {
            console.error("ðŸš¨ Error refreshing token:", error);
            return null;
        }
    };

    // âœ… Fetch accounts and handle token expiration
    const fetchAccounts = async () => {
        let token = localStorage.getItem("access_token");

        const fetchData = async (token) => {
            return fetch("http://127.0.0.1:8000/api/v1/accounts/", {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
            });
        };

        let response = await fetchData(token);

        if (response.status === 401) {  // ðŸ”¥ Token expired, refresh and retry
            console.warn("âš ï¸ Access token expired. Refreshing...");
            token = await refreshToken();
            if (token) {
                response = await fetchData(token);
            }
        }

        if (!response.ok) {
            console.error("ðŸš¨ Error fetching accounts:", response.status);
            setAccounts([]);
            setLoading(false);
            return;
        }

        const data = await response.json();
        console.log("âœ… Raw API Response:", data); // Debugging Log

        if (data.results && Array.isArray(data.results) && data.results.length > 0) {
            setAccounts(data.results); // ðŸ”¥ Extract accounts from "results"
            setSelectedAccount(data.results[0].name); // Auto-select first account
            fetchAccountBalance(data.results[0].name);
        } else {
            console.warn("âš ï¸ No accounts found.");
            setAccounts([]);
        }
        setLoading(false);
    };


    // âœ… Fetch balance of the selected account (with token refresh)
    const fetchAccountBalance = async (accountName) => {
        setLoading(true); // Start loading
        let token = localStorage.getItem("access_token");

        const fetchData = async (token) => {
            return fetch(`http://127.0.0.1:8000/api/v1/accounts/balance/${encodeURIComponent(accountName)}/`, {
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
            });
        };

        let response = await fetchData(token);

        if (response.status === 401) { // ðŸ”¥ Token expired, refresh and retry
            console.warn("âš ï¸ Access token expired. Refreshing...");
            token = await refreshToken();
            if (token) {
                response = await fetchData(token);
            }
        }

        if (!response.ok) {
            console.error("ðŸš¨ Error fetching account balance:", response.status);
            setLoading(false);
            return;
        }

        const data = await response.json();
        setBalance(data.accountBalance);
        setCurrency(data.currency);
        setLoading(false);
    };

    // âœ… Handle account selection
    const handleAccountChange = (event) => {
        const newAccount = event.target.value;
        setSelectedAccount(newAccount);
        fetchAccountBalance(newAccount);
    };

    // âœ… Fetch accounts when component loads
    useEffect(() => {
        fetchAccounts();
    }, []);

    // âœ… Styling for the balance card
    const cardStyle = {
        padding: "20px",
        textAlign: "center",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        background: "#ffffff",
    };

    return (
        <Grid container spacing={3} justifyContent="center">
            {/* Account Dropdown */}
            <Grid item xs={12} sm={8} md={6}>
                <FormControl fullWidth>
                    <InputLabel>Selecciona una cuenta</InputLabel>
                    <Select 
                        value={selectedAccount || ""} 
                        onChange={handleAccountChange} 
                        disabled={accounts.length === 0} // Disable if no accounts
                    >
                        {accounts.length > 0 ? (
                            accounts.map((account) => (
                                <MenuItem key={account.id} value={account.name}>
                                    {account.name}
                                </MenuItem>
                            ))
                        ) : (
                            <MenuItem disabled>No accounts available</MenuItem>
                        )}
                    </Select>
                </FormControl>
            </Grid>

            {/* Balance Display */}
            <Grid item xs={12} sm={8} md={6}>
                <Paper style={cardStyle}>
                    <Typography variant="h5" color="primary">
                        {selectedAccount ? selectedAccount : "Seleccione una cuenta"}
                    </Typography>
                    <Typography variant="h4">
                        {loading ? "Cargando..." : balance !== null ? `${balance.toLocaleString()} ${currency}` : "Sin saldo"}
                    </Typography>
                </Paper>
            </Grid>
        </Grid>
    );
};

export default BalanceCard;
